{
  "input" :
  {
     "ios-cli:command" : "conf t
ipv6 access-list {{flow_prereq_ipv6_acl1}}
10 deny ipv6 any any
exit
ipv6 access-list {{flow_prereq_ipv6_acl2}}
20 deny ipv6 any any
exit
ipv4 access-list {{flow_prereq_ipv4_acl1}}
10 deny ipv4 any any
exit
ipv4 access-list {{flow_prereq_ipv4_acl2}}
20 deny ipv4 any any
exit
flow monitor-map {{flow_prereq_ipv6_monitor1}}
record ipv6
exit
flow monitor-map {{flow_prereq_ipv6_monitor2}}
record ipv6
exit
flow monitor-map {{flow_prereq_ipv4_monitor1}}
record ipv4
exit
flow monitor-map {{flow_prereq_ipv4_monitor2}}
record ipv4 peer-as
exit
sampler-map {{flow_prereq_sampler1}}
random 1 out-of 10000
exit
sampler-map {{flow_prereq_sampler2}}
random 1 out-of 10
exit
sampler-map {{flow_prereq_sampler1}}1
random 1 out-of 10000
exit
sampler-map {{flow_prereq_sampler2}}2
random 1 out-of 10
exit
commit
end"
  }
}

{
  "input" :
  {
     "ios-cli:command" : "conf t
interface {{eth_intf-id}}
description testing interface
no shutdown
commit
end"
  }
}

{
  "input" :
  {
     "ios-cli:command" : "conf t
no ipv6 access-list {{flow_prereq_ipv6_acl1}}
no ipv6 access-list {{flow_prereq_ipv6_acl2}}
no ipv4 access-list {{flow_prereq_ipv4_acl1}}
no ipv4 access-list {{flow_prereq_ipv4_acl2}}
no flow monitor-map {{flow_prereq_ipv6_monitor1}}
no flow monitor-map {{flow_prereq_ipv6_monitor2}}
no flow monitor-map {{flow_prereq_ipv4_monitor1}}
no flow monitor-map {{flow_prereq_ipv4_monitor2}}
no sampler-map {{flow_prereq_sampler1}}
no sampler-map {{flow_prereq_sampler2}}
no sampler-map {{flow_prereq_sampler1}}1
no sampler-map {{flow_prereq_sampler2}}2
commit
end"
  }
}

{
  "input" :
  {
     "ios-cli:command" : "conf t
interface {{eth_intf-id}}
description testing interface
no shutdown
commit
end"
  }
}

{
  "input" :
  {
     "ios-cli:command" : "configure
set firewall family inet filter {{iacl_in-acl-name1}} term term1 from address 20.20.20.21/32
set firewall family inet filter {{iacl_in-acl-name2}} term term2 from address 20.20.20.22/32
set firewall family inet filter {{iacl_out-acl-name1}} term term3 from address 20.20.20.23/32
set firewall family inet filter {{iacl_out-acl-name2}} term term4 from address 20.20.20.24/32
commit
exit"
  }
}

{
  "input" :
  {
     "ios-cli:command" : "conf t
     ipv4 access-list {{iacl_in-acl-name3}}
     deny ipv4 0.0.0.0 0.255.255.255 any
     permit ipv4 any any
     commit
     end"
  }
}

{
  "input" :
  {
     "ios-cli:command" : "conf t
     no ipv4 access-list {{iacl_in-acl-name3}}
     commit
     end"
  }
}
