{
  "input" :
  {
     "ios-cli:command" : "conf t
 class-map match-all {{qos_class_name3}}
 match qos-group {{qos_group}}
 match mpls experimental topmost {{qos_mpls_topmost_min}}
 match precedence {{qos_precedence5}}
 match access-group ipv4 {{qos_class_name3}}
 end-class-map
 commit
 end"
  }
}

{
  "input" :
  {
     "ios-cli:command" : "conf t
 no class-map match-all {{qos_class_name3}}
 commit
 end"
  }
}

{
  "input" :
  {
     "ios-cli:command" : "conf t
class-map match-any {{qos_class_name5}}
 match mpls experimental topmost {{qos_mpls_topmost_min}} 
 match precedence {{qos_precedence0}} 
 end-class-map
policy-map {{qos_policy_name3}}
 class {{qos_class_name5}}
  set qos-group {{qos_set_group}}
  set mpls experimental topmost {{qos_set_mpls_topmost_upd}}
  set precedence {{qos_precedence0}}
  priority level {{qos_weight_priority}} 
 exit
 class class-default
  set qos-group {{qos_set_group}}
  set mpls experimental topmost {{qos_set_mpls_topmost_upd}}
  set precedence {{qos_precedence0}}
  police rate percent 1 
  exit 
  queue-limit {{qos_queue_limit_ms}} ms 
  bandwidth remaining percent {{bandwidth_percent_remaining}} 
 exit 
 end-policy-map 
 commit
 end"
  }
}

{
  "input" :
  {
     "ios-cli:command" : "conf t
 no policy-map {{qos_policy_name3}}
 no class-map match-any {{qos_class_name5}}
 commit
 end"
  }
}
