{
  "input" :
  {
     "ios-cli:command" : "conf t
router static
address-family ipv4 unicast
{{ip-route}} {{ip-route2}}
{{ip-route}} {{static_route_test_interface}} {{ip-route3}} metric 2
exit
address-family ipv6 unicast
{{ip61}} {{static_route_test_interface}} {{ip62}}
commit
exit
exit
end"
  }
}

{
  "input" :
  {
     "ios-cli:command" : "conf t
     no router static
     commit
     end"
  }
}

{
  "input" :
