{
  "input" :
  {
     "ios-cli:command" : "policy-map {{output_policy}}
 class class-default
  shape average percent 50
  exit
  end-policy-map
policy-map {{input_policy}}
 class class-default
  shape average percent 50
  exit
  end-policy-map
policy-map {{output_policy2}}
 class class-default
  shape average percent 50
  exit
  end-policy-map
policy-map {{input_policy2}}
 class class-default
  shape average percent 50
  exit
  end-policy-map
"
  }
