     "ios-cli:command" : "conf t
interface {{lacp_intf-id}}
bundle id {{lacp_bundle_id_nr}} mode {{lacp_mode_xr_active}}
lacp period short
commit
end"
  }
}

{
  "input" :
  {
     "ios-cli:command" : "conf t
interface {{lacp_intf-id}}
no bundle id {{lacp_bundle_id_nr}} mode {{lacp_mode_xr_active}}
no lacp period short
commit
end"
  }
}

{
  "input" :
  {
     "ios-cli:command" : "configure
set interfaces {{lacp_intf-id}} gigether-options 802.3ad {{lacp_bundle_id}}
set interfaces {{lacp_bundle_id}} aggregated-ether-options lacp active
set interfaces {{lacp_bundle_id}} aggregated-ether-options lacp periodic fast
commit
exit"
     }
}

  {
  "input" :
  {
     "ios-cli:command" : "configure
delete interfaces {{lacp_bundle_id}} aggregated-ether-options
delete interfaces {{lacp_intf-id}} gigether-options
commit
exit"
     }
}
